          <div className='form-control'>
              <label>Task </label>
              <input type="text" placeholder='Add Task' 
              value={text}
              onChange ={(e) =>setText(e.target.value)}
              />
          </div>
          <div className='form-control'>
              <label>Day & Time </label>
