import React from 'react'
import Task from './Task'


const Tasks = ({tasks,onDelete}) => {

    return (
        <>
         {
          tasks.map((task) =>(
            <Task key={task.id} task ={task} onDelete={onDelete}></Task>

            ))
        }  

        </>
    )
}

export default Tasks
